interface WeatherData {
    coord: { lon: number; lat: number };
    weather: { id: number; main: string; description: string; icon: string }[];
    base: string;
    main: {
      temp: number;
      feels_like: number;
      temp_min: number;
      temp_max: number;
      pressure: number;
      humidity: number;
      sea_level?: number; // Dodan optionalan klju훾 sea_level
      grnd_level?: number; // Dodan optionalan klju훾 grnd_level
    };
    visibility: number;
    wind: { speed: number; deg: number; gust?: number }; // Dodan optionalan klju훾 gust
    rain?: { "1h": number }; // Dodan optionalan klju훾 rain
    clouds: { all: number };
    dt: number;
    sys: {
      type: number;
      id: number;
      country: string;
      sunrise: number;
      sunset: number;
    };
    timezone: number;
    id: number;
    name: string;
    cod: number;
  }